package com.xingkong1983.star.biz;import lombok.Data;import lombok.EqualsAndHashCode;@Data@EqualsAndHashCode(callSuper = false)public class BizException extends RuntimeException {	private static final long serialVersionUID = 1L;	/**	 * 错误编码	 */	private int code;	/**	 * 操作信息	 */	private String message;	/**	 * 错误日志	 */	private String log;	/**	 * 数据	 */	private Object data;	public BizException(Throwable cause) {		super(cause);		this.code = 500;		this.message = cause.getMessage();	}	public BizException(int code, String message) {		super(message);		this.code = code;		this.message = message;			}	public BizException(int code, String message, Object data) {		super(message);		this.code = code;		this.message = message;		this.data = data;			}	public BizException(int code, Throwable cause) {		super(cause.getMessage(), cause);		this.code = code;	}	public BizException(int code, Throwable cause, Object data) {		super(cause.getMessage(), cause);		this.code = code;		this.message = cause.getMessage();		this.data = data;			}}